import React from 'react';
import './ImageLinkForm.css';

const ImageLinkForm = ({ onInputChange, onButtonSubmit }) => {
	return (
		<div>
			<p className="f3">
				{'Smart Brain will detect faces in your photos. Give it a try!'}
			</p>
			<div className="center">
				<div className="form pa4 br3 shadow-5">
					<div>
						<input
							className="f4 pa2 w-70"
							type="text"
							placeholder="paste an image URL to scan"
							onChange={onInputChange}
						/>
						<button
							className="w-30 grow f4 link ph3 pv2 dib b white bg-hot-pink"
							onClick={onButtonSubmit}
						>
							SCAN
						</button>
					</div>
					<div>
						<input
							className="f4 pa2 w-70"
							type="file"
							id="myFile"
							onChange={onInputChange}
						></input>
						<button
							className="w-30 grow f4 link ph3 pv2 dib b white bg-hot-pink"
							onClick={onButtonSubmit}
						>
							SCAN
						</button>
					</div>
				</div>
			</div>
		</div>
	);
};

export default ImageLinkForm;
